            List prepare raster line

Usable with IRtcMoF

Not usable with IRtcAutoLaserControl, IRtcWobbel or IRtcSkywriting

            var rtcRaster = rtc as IRtcRaster;
            var image = System.Drawing.Bitmap.FromFile("sample.bmp") as Bitmap;
            int px = bitmap.Width;
            int py = bitmap.Height;
            float pitch = 0.01f; // Pixel pitch = 10um
            double pixelPeriod = 50; // Pixel period: 50 usec (0.00005 s)
            double pixelTime = 20; // Max. pixel time: 20 usec (0.00002 s)
            bool inverted = false; // Invert black and white color
            rtc.ListBegin();
            for (int y = 0; y < py; y++)
            {
                rtc.ListJumpTo(new Vector2(0, py * pitch));
                rtcRaster.ListRasterLine(pixelPeriod, xPitch, (uint)px);
                for (int x = 0; x < px; x++)
                {
                    var color = bitmap.GetPixel(x, py - y - 1);
                    double grayRatio = 1.0 - (0.3 * color.R + 0.59 * color.G + 0.11 * color.B) / 255.0; // RGB to gray scale
                    if (inverted)
                        grayRatio = 1.0f - grayRatio;
                    rtcRaster.ListRasterPixel(pixelTime * grayRatio);
                }
            }
            rtc.ListEnd();
            rtc.ListExecute(true);

