    D2 = 2,
    D3 = 3,
    D4 = 4,
    D5 = 5,
    D6 = 6,
    D7 = 7,
    D8 = 8,
    D9 = 9,
    D10 = 10,
    D11 = 11,
    D12 = 12,
    D13 = 13,
    D14 = 14,
    D15 = 15,
}
var dInput = IOFactory.CreateInputExtension1(rtc);

// 1. by manually
dInput.Initialize();
do
{
    // read and update input channel(s) 
    dInput.Update();
    ...
    if (dIn.IsAOn(DIN.D0))
    {
        ... something
    }
}
while(true);

// 2. by event 
dInput.OnChanged += DIn_OnChanged;
do
{
    // read and update input channel(s) 
    // also 'OnChanged' event handler has called
    dInput.Update();
}
while(true);
...
void DIn_OnChanged(IDInput dInput, int bitNo, SignalEdges edge)
{
     switch (bitNo)
     {
         case 0: 
            if (edge == SignalEdges.High)
            { 
                ... something
            }
            break;
     }
}

