Initialize

bool success = true;
double maxWatt = 20;
int analogCh = 1; //or 2
var laser = LaserFactory.CreateVirtualAnalog(0, maxWatt, analogCh);
laser.Rtc = rtc;
success &=laser.Initialize();
if (laser is ILaserPowerControl powerControl)
    success &= powerControl.CtlPower(laser.MaxPowerWatt * 0.1); // 10% (= 1V)
if (laser is ILaserGuideControl guideControl)
    success &= guideControl.CtlGuide(true); // To turn guide laser beam on
Debug.Assert(success);

List begin

bool success = true;
success &= rtc.ListBegin();
success &= laser.ListBegin();
success &= laser.ListPower(5); // output power to 5W
success &= rtc.ListJumpTo(new Vector2(radius, 0));
success &= rtc.ListArcTo(Vector2.Zero, 360); // 1st circle
success &= laser.ListPower(10); // output power to 10W
success &= rtc.ListJumpTo(new Vector2(-radius, 0)); // 2nd circle
success &= rtc.ListArcTo(Vector2.Zero, 360);
success &= rtc.ListJumpTo(Vector2.Zero);
success &= laser.ListEnd();
success &= rtc.ListEnd();
if (success)
    success &= rtc.ListExecute(true);

